import React, { useState, useEffect } from 'react';
import { Search, Music, Disc, User, Clock, Award } from 'lucide-react';

// Spotify API constants
const SPOTIFY_CLIENT_ID = 'ec65a3e0c919492c863a40dc1fb23c8e'; // Replace with your actual client ID
const REDIRECT_URI = 'http://localhost:3000/callback'; // Adjust to your app's redirect URI
const AUTH_ENDPOINT = 'https://accounts.spotify.com/authorize';
const RESPONSE_TYPE = 'token';
const SCOPES = 'user-read-private user-read-email';

// Helper function to get token from URL on callback
const getTokenFromUrl = () => {
  const hash = window.location.hash;
  if (!hash) return null;

  const token = hash
    .substring(1)
    .split('&')
    .find(elem => elem.startsWith('access_token'))
    ?.split('=')[1];

  // Clean the URL by removing the hash
  window.location.hash = '';
  
  return token;
};

// Main App component
const SpotifyApp = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(null);
  const [loading, setLoading] = useState(false);
  const [token, setToken] = useState(null);

  // Check for token on component mount
  useEffect(() => {
    // Check if we already have a token in localStorage
    const savedToken = localStorage.getItem('spotify_token');
    const tokenExpiryStr = localStorage.getItem('spotify_token_expiry');
    const tokenExpiry = tokenExpiryStr ? parseInt(tokenExpiryStr) : 0;
    
    // Check for token in URL (after redirect from Spotify)
    const tokenFromUrl = getTokenFromUrl();
    
    if (tokenFromUrl) {
      // Save the new token to localStorage with expiry (1 hour from now)
      const expiryTime = Date.now() + 3600 * 1000;
      localStorage.setItem('spotify_token', tokenFromUrl);
      localStorage.setItem('spotify_token_expiry', expiryTime.toString());
      setToken(tokenFromUrl);
    } else if (savedToken && tokenExpiry > Date.now()) {
      // Use existing token if it's still valid
      setToken(savedToken);
    }
  }, []);

  // Function to initiate Spotify login
  const login = () => {
    window.location.href = `${AUTH_ENDPOINT}?client_id=${SPOTIFY_CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${encodeURIComponent(SCOPES)}`;
  };

  // Function to logout
  const logout = () => {
    localStorage.removeItem('spotify_token');
    localStorage.removeItem('spotify_token_expiry');
    setToken(null);
    setSelectedArtist(null);
  };

  // Fetch artist data from Spotify API
  const searchSpotify = async (query) => {
    if (!token || query.trim() === '') {
      setSearchResults([]);
      return;
    }

    setLoading(true);
    
    try {
      const response = await fetch(`https://api.spotify.com/v1/search?q=${query}&type=artist&limit=5`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      
      if (!response.ok) {
        if (response.status === 401) {
          // Token expired
          logout();
          throw new Error('Session expired. Please login again.');
        }
        throw new Error('Failed to fetch data from Spotify');
      }
      
      const data = await response.json();
      setSearchResults(data.artists.items);
    } catch (error) {
      console.error('Error searching Spotify:', error);
      setSearchResults([]);
    } finally {
      setLoading(false);
    }
  };

  // Search debounce effect
  useEffect(() => {
    if (searchQuery.trim() === '') {
      setSearchResults([]);
      return;
    }

    const timeoutId = setTimeout(() => {
      searchSpotify(searchQuery);
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [searchQuery, token]);

  // Function to get artist details
  const fetchArtistDetails = async (artistId) => {
    if (!token) return null;
    
    setLoading(true);
    
    try {
      // Get basic artist info
      const artistResponse = await fetch(`https://api.spotify.com/v1/artists/${artistId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      
      if (!artistResponse.ok) throw new Error('Failed to fetch artist data');
      const artistData = await artistResponse.json();
      
      // Get top tracks
      const tracksResponse = await fetch(`https://api.spotify.com/v1/artists/${artistId}/top-tracks?market=US`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      
      if (!tracksResponse.ok) throw new Error('Failed to fetch top tracks');
      const tracksData = await tracksResponse.json();
      
      // Get albums
      const albumsResponse = await fetch(`https://api.spotify.com/v1/artists/${artistId}/albums?include_groups=album&limit=10&market=US`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      
      if (!albumsResponse.ok) throw new Error('Failed to fetch albums');
      const albumsData = await albumsResponse.json();
      
      // Format the data
      const formattedArtist = {
        ...artistData,
        topTracks: tracksData.tracks.map(track => ({
          name: track.name,
          duration: formatDuration(track.duration_ms),
          popularity: track.popularity
        })),
        albums: albumsData.items.map(album => ({
          id: album.id,
          name: album.name,
          year: new Date(album.release_date).getFullYear(),
          tracks: album.total_tracks,
          images: album.images
        }))
      };
      
      return formattedArtist;
    } catch (error) {
      console.error('Error fetching artist details:', error);
      return null;
    } finally {
      setLoading(false);
    }
  };

  // Helper to format milliseconds to mm:ss
  const formatDuration = (ms) => {
    const minutes = Math.floor(ms / 60000);
    const seconds = ((ms % 60000) / 1000).toFixed(0);
    return `${minutes}:${seconds.padStart(2, '0')}`;
  };

  // Handle artist selection
  const handleArtistSelect = async (artist) => {
    const detailedArtist = await fetchArtistDetails(artist.id);
    if (detailedArtist) {
      setSelectedArtist(detailedArtist);
      setSearchResults([]);
      setSearchQuery('');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-950 to-black text-white overflow-hidden">
      {/* Header */}
      <header className="sticky top-0 bg-black/80 backdrop-blur-md z-10 p-4 border-b border-gray-800/50 shadow-lg">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <Music className="w-8 h-8 text-green-500 mr-2 animate-pulse" />
            <h1 className="text-xl font-bold">Spotify Artist Lookup</h1>
          </div>
          
          {token ? (
            <div className="flex items-center">
              <div className="relative w-64 md:w-96 mr-4">
                <div className="absolute inset-y-0 left-0 flex items-center pl-3">
                  <Search className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  type="text"
                  className="w-full bg-gray-800/80 rounded-full py-2 pl-10 pr-4 text-sm focus:outline-none focus:ring-2 focus:ring-green-500"
                  placeholder="Search for an artist..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
                {loading && (
                  <div className="absolute right-3 top-2">
                    <div className="animate-spin h-5 w-5 border-2 border-green-500 border-t-transparent rounded-full"></div>
                  </div>
                )}
                {searchResults.length > 0 && (
                  <div className="absolute mt-1 w-full bg-gray-900/90 backdrop-blur-md rounded-md shadow-lg z-20 overflow-hidden">
                    <ul>
                      {searchResults.map(artist => (
                        <li 
                          key={artist.id}
                          className="px-4 py-2 hover:bg-gray-800/90 cursor-pointer flex items-center transition-all duration-300 hover:translate-x-1"
                          onClick={() => handleArtistSelect(artist)}
                        >
                          <div className="w-10 h-10 bg-gray-700 rounded-full mr-3 flex-shrink-0 overflow-hidden">
                            {artist.images && artist.images.length > 0 ? (
                              <img 
                                src={artist.images[artist.images.length - 1].url} 
                                alt={artist.name}
                                className="w-full h-full object-cover" 
                              />
                            ) : (
                              <User className="w-6 h-6 text-gray-300 m-auto mt-2" />
                            )}
                          </div>
                          <div>
                            <p className="font-medium">{artist.name}</p>
                            <p className="text-sm text-gray-400">Artist</p>
                          </div>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
              <button 
                onClick={logout}
                className="bg-gray-800 hover:bg-gray-700 text-white px-4 py-2 rounded-full text-sm transition-colors duration-300"
              >
                Logout
              </button>
            </div>
          ) : (
            <button 
              onClick={login}
              className="bg-green-600 hover:bg-green-500 text-white px-6 py-2 rounded-full text-sm font-medium transition-colors duration-300"
            >
              Connect with Spotify
            </button>
          )}
        </div>
      </header>

      {/* Main Content */}
      <main className="p-4">
        {!token ? (
          <div className="max-w-2xl mx-auto text-center py-16">
            <Music className="w-16 h-16 text-green-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold mb-2">Connect with Spotify</h2>
            <p className="text-gray-400 mb-8">Login with your Spotify account to search for artists</p>
            <button 
              onClick={login}
              className="bg-green-600 hover:bg-green-500 text-white px-8 py-3 rounded-full font-medium transition-colors duration-300"
            >
              Connect with Spotify
            </button>
          </div>
        ) : selectedArtist ? (
          <div className="max-w-5xl mx-auto">
            {/* Artist Header */}
            <div className="flex flex-col md:flex-row items-center md:items-end mb-8 p-6 bg-gradient-to-b from-gray-800/80 to-black rounded-lg shadow-lg">
              <div className="w-48 h-48 rounded-full mb-4 md:mb-0 md:mr-6 flex-shrink-0 overflow-hidden">
                {selectedArtist.images && selectedArtist.images.length > 0 ? (
                  <img 
                    src={selectedArtist.images[0].url} 
                    alt={selectedArtist.name}
                    className="w-full h-full object-cover" 
                  />
                ) : (
                  <div className="w-full h-full bg-gray-700 flex items-center justify-center">
                    <User className="w-24 h-24 text-gray-300" />
                  </div>
                )}
              </div>
              <div>
                <span className="text-sm font-medium">Artist</span>
                <h1 className="text-5xl font-bold mb-2">{selectedArtist.name}</h1>
                <div className="flex flex-wrap items-center text-sm text-gray-300">
                  <span className="mr-2">{selectedArtist.followers.total.toLocaleString()} followers</span>
                  <span className="mr-2">â€¢</span>
                  <span className="flex items-center">
                    <span className="inline-block w-4 h-4 bg-green-500 rounded-full mr-1"></span>
                    Popularity: {selectedArtist.popularity}/100
                  </span>
                </div>
              </div>
            </div>

            {/* Genres */}
            <div className="mb-8">
              <h2 className="text-xl font-bold mb-4">Genres</h2>
              <div className="flex flex-wrap gap-2">
                {selectedArtist.genres.length > 0 ? (
                  selectedArtist.genres.map((genre, index) => (
                    <span 
                      key={index}
                      className="bg-gray-800/80 rounded-full px-3 py-1 text-sm shadow-md transition-all duration-300 hover:bg-gray-700/80"
                    >
                      {genre}
                    </span>
                  ))
                ) : (
                  <span className="text-gray-400">No genres available</span>
                )}
              </div>
            </div>

            {/* Top Tracks */}
            <div className="mb-8">
              <h2 className="text-xl font-bold mb-4">Popular Tracks</h2>
              <div className="bg-gray-900/70 rounded-md shadow-lg">
                {selectedArtist.topTracks.length > 0 ? (
                  selectedArtist.topTracks.map((track, index) => (
                    <div 
                      key={index}
                      className="flex items-center px-4 py-3 hover:bg-gray-800/90 border-b border-gray-800/50 last:border-none transition-all duration-300 group hover:translate-x-2"
                    >
                      <div className="w-6 text-gray-400 mr-4">{index + 1}</div>
                      <div className="flex-grow">
                        <p className="font-medium">{track.name}</p>
                      </div>
                      <div className="text-gray-400 flex items-center mr-4">
                        <Award className="w-4 h-4 mr-1 text-gray-500" />
                        {track.popularity}
                      </div>
                      <div className="text-gray-400 flex items-center group-hover:text-green-500 transition-colors duration-300">
                        <Clock className="w-4 h-4 mr-2" />
                        {track.duration}
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="p-4 text-gray-400">No tracks available</div>
                )}
              </div>
            </div>

            {/* Albums */}
            <div>
              <h2 className="text-xl font-bold mb-4">Albums</h2>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                {selectedArtist.albums.length > 0 ? (
                  selectedArtist.albums.map((album, index) => (
                    <div key={index} className="bg-gray-900/80 p-4 rounded-md hover:bg-gray-800/90 transition-all duration-300 shadow-lg">
                      <div className="aspect-square bg-gray-800/80 mb-4 rounded flex items-center justify-center shadow-inner overflow-hidden">
                        {album.images && album.images.length > 0 ? (
                          <img 
                            src={album.images[0].url} 
                            alt={album.name}
                            className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500" 
                          />
                        ) : (
                          <Disc className="w-16 h-16 text-gray-700" />
                        )}
                      </div>
                      <h3 className="font-medium truncate">{album.name}</h3>
                      <p className="text-sm text-gray-400">{album.year} â€¢ {album.tracks} tracks</p>
                    </div>
                  ))
                ) : (
                  <div className="col-span-4 text-gray-400">No albums available</div>
                )}
              </div>
            </div>

            {/* Back button */}
            <div className="mt-8 text-center">
              <button 
                className="bg-gray-800 hover:bg-gray-700 text-white px-6 py-2 rounded-full text-sm transition-colors duration-300"
                onClick={() => setSelectedArtist(null)}
              >
                Back to Search
              </button>
            </div>
          </div>
        ) : (
          <div className="max-w-2xl mx-auto text-center py-16">
            <Music className="w-16 h-16 text-green-500 mx-auto mb-4" />
            <h2 className="text-2xl font-bold mb-2">Search for your favorite artists</h2>
            <p className="text-gray-400 mb-8">Enter an artist name in the search bar above to see their details</p>
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="mt-8 p-6 border-t border-gray-800/30 text-center text-sm text-gray-500">
        <p>Spotify Artist Lookup App</p>
        <p className="mt-1">Connected to the Spotify API</p>
      </footer>

      {/* CSS for animations */}
      <style jsx global>{`
        @keyframes float {
          0% { transform: translateY(0px); }
          50% { transform: translateY(-10px); }
          100% { transform: translateY(0px); }
        }

        @keyframes fadeIn {
          0% { opacity: 0; transform: translateY(10px); }
          100% { opacity: 1; transform: translateY(0); }
        }

        .animate-float {
          animation: float 3s ease-in-out infinite;
        }

        .animate-fadeIn {
          animation: fadeIn 0.5s ease-out forwards;
        }
      `}</style>
    </div>
  );
};

export default SpotifyApp;
